import { Component } from '@angular/core';
import {FormBuilder, FormControl, FormGroup, ReactiveFormsModule, Validators} from "@angular/forms";
import {BehaviorSubject} from "rxjs";

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  standalone: true,
  imports: [
    ReactiveFormsModule
  ],
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {
  registrationForm: FormGroup;

  constructor(private formBuilder: FormBuilder) {
    this.registrationForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      username: ['', Validators.required],
      password: ['', Validators.required],
      confirmPassword: ['', Validators.required],
    }, {
      validators: this.passwordMatchValidator
    });
  }

  onSubmit() {
    if (this.registrationForm.valid) {
      // Выполните действия при отправке формы
    }
  }

  passwordMatchValidator(formGroup: FormGroup) {
    const password = formGroup.get('password')!.value;
    const confirmPassword = formGroup.get('confirmPassword')!.value;

    if (password !== confirmPassword) {
      formGroup.get('confirmPassword')!.setErrors({ passwordMismatch: true });
    } else {
      formGroup.get('confirmPassword')!.setErrors(null);
    }
  }

}
<ng-container>
  <div class="flex flex-col items-center w-full bg-sky-950 justify-center" style="height: calc(100vh - 5rem)">
    <form [formGroup]="registrationForm" (ngSubmit)="onSubmit()" class="flex flex-col bg-white rounded w-4/12 p-4">
      <div class="mb-3">
        <label for="inputEmail" class="form-label">Email address</label>
        <input formControlName="email" type="email" class="border-blue-300 form-control" id="inputEmail" aria-describedby="emailHelp">
        <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
      </div>
      <div class="mb-3">
        <label for="inputUsername" class="form-label">Username</label>
        <input formControlName="username" type="text" class="border-blue-300 form-control" id="inputUsername" aria-describedby="emailHelp">
      </div>
      <div class="mb-3">
        <label for="inputPassword1" class="form-label">Password</label>
        <input formControlName="password" type="password" class="border-blue-300  form-control" id="inputPassword1">
      </div>

      <div class="mb-3">
        <label for="inputPassword2" class="form-label">Confirm password</label>
        <input formControlName="confirmPassword" type="password" class="border-blue-300  form-control" id="inputPassword2">
        <div class="text-danger" *ngIf="registrationForm.hasError('passwordMismatch')">
          Passwords do not match
        </div>
      </div>

      <button type="submit" class="btn btn-primary bg-sky-700" [disabled]="registrationForm.invalid">Submit</button>
    </form>
  </div>
</ng-container>
