import {Component, Input, OnInit} from '@angular/core';
import {BracketService} from "../../../../../services/BracketService";
import {BracketData} from "../../../../../models/request/BracketData";
import {NgForOf, NgIf, NgStyle} from "@angular/common";
import {StageData} from "../../../../../models/request/StageData";
import {last} from "rxjs";

@Component({
  selector: 'app-bracket',
  standalone: true,
  templateUrl: './bracket.component.html',
  imports: [
    NgIf,
    NgForOf,
    NgStyle
  ],
  styleUrls: ['./bracket.component.css']
})
export class BracketComponent implements OnInit{
  @Input() bracketId: number | undefined;

  public bracket: BracketData | undefined;

  constructor(private bracketService: BracketService,
              ) {
  }
  ngOnInit(): void {
      this.bracketService.findBracket(this.bracketId).subscribe(value => {
        this.bracket = value
      })
  }


  protected readonly last = last;
}
<ng-container *ngIf="bracket">
  <div class="max-w-5/6 w-5/6 overflow-x-scroll overflow-y-scroll">
    <div [ngStyle]="{'width': 30 * (bracket.stages.length+1) + 'vw'}" class=" self-center flex justify-around items-center border-2 border-blue-700 " style="max-height: 75vh; min-height: 75vh;">
      <div  class="flex flex-col items-center justify-around" style="min-height: 75vh; width: 30vw" *ngFor="let stage of bracket.stages">
        <div class="flex flex-col w-5/6 h-20 bg-sky-400 " *ngFor="let match of stage.matches; let matchIndex = index">
          <div class="flex flex-col w-full h-50 " *ngIf="match.teams && match.teams.length > 0; else emptyBlock">
            <!-- Відображення команд, якщо вони є -->
            <div class="flex w-full h-full items-center justify-center" *ngFor="let team of match.teams; let last = last">
              {{team.name}}
            </div>
            <!-- Додайте роздільну лінію, якщо не остання команда в матчі -->
            <div *ngIf="!last" class="border-b border-gray-500"></div>
          </div>
          <ng-template #emptyBlock>
            <!-- Відображення пустого блоку, якщо немає команд в матчі -->
            <div class="flex w-full h-50 items-center justify-center">
              Empty Block
            </div>
            <!-- Додайте роздільну лінію, якщо це не останній матч -->
            <div *ngIf="matchIndex < stage.matches.length - 1" class="border-b border-gray-500"></div>
          </ng-template>
        </div>
      </div>
      <div class="flex flex-col w-2/5 items-center justify-center" style="min-height: 75vh" >
        <div class="flex flex-col w-80 h-3/5 bg-sky-400">
          <div class="flex flex-col w-full h-50">
            <p>Winner</p>
          </div>
        </div>
      </div>
    </div>
  </div>
</ng-container>
