import {Component, OnInit} from '@angular/core';
import {AllTournamentRequestData} from "../../../../models/request/AllTournamentRequestData";
import {TournamentService} from "../../../../services/TournamentService";
import {AsyncPipe, NgForOf, NgIf, NgStyle} from "@angular/common";
import {Observable} from "rxjs";
import {TournamentRequestData} from "../../../../models/request/TournamentRequestData";

@Component({
  selector: 'app-tournaments-all',
  templateUrl: './tournaments-all.component.html',
  standalone: true,
  imports: [
    NgForOf,
    AsyncPipe,
    NgIf,
    NgStyle
  ],
  styleUrls: ['./tournaments-all.component.css']
})
export class TournamentsAllComponent implements OnInit{

  public tournaments$: Observable<TournamentRequestData[]> = this.tournamentService.getALlTournaments();
  public logoDataMap: { [key: number]: string } = {};
  public isHasLogoMap: {[key: number] : boolean} = {}

  constructor(private tournamentService: TournamentService) {
  }

  ngOnInit(): void {
    this.loadLogos()
  }


  loadLogos(): void {
    this.tournaments$.forEach(value => {
      value.forEach(value1 => {
        this.tournamentService.getTournamentLogo(value1.id).subscribe(
          value2 => {
            this.logoDataMap[value1.id] = 'data:image/jpeg;base64,' + value2.data
            this.isHasLogoMap[value1.id] = true;
          }
          ,error => {
            this.isHasLogoMap[value1.id] = false
          }
        )
      })
    })

  }


  getLogo(id: number): string {
    return this.logoDataMap[id]
  }



}
<ng-container *ngIf="tournaments$ | async as tournaments">
  <div class="flex flex-col" style="min-height: calc(100vh - 5rem);
                                             background: rgb(2,0,36);
                                             background: linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(3,56,91,1) 81%);">
    <div class="flex mt-4" style="margin-left: 15%">
      <i class="fa-solid fa-trophy text-5xl text-cyan-500" style="margin-right: 5%"></i>
      <p class="text-4xl text-blue-200 font-bold">Tournaments</p>
    </div>
    <div class="flex justify-center">
      <select>
        <option selected class="form-select">Upcoming</option>
      </select>
    </div>
    <div class="grid mx-5 xl:grid-cols-3 md:grid-cols-2 sm:grid-cols-1 gap-10" [ngStyle] ="{'height' : 'calc(300px * '+tournaments.length/3}">
      <div class="flex flex-col h-3/4" *ngFor="let tournament of tournaments">
        <div class="w-full " style="background-size: cover;background-position: center; height: 25vh" [class.noLogo] ="!isHasLogoMap[tournament.id]" [ngStyle]="{'background-image': 'url(' + getLogo(tournament.id) + ')'}"></div>
      </div>
    </div>
  </div>
</ng-container>
