import {Component, OnInit} from '@angular/core';
import {Router} from "@angular/router";
import {NgIf} from "@angular/common";
import {JwtUtil} from "../../util/JwtUtil";
import {AuthService} from "../../services/AuthService";

@Component({
  selector: 'app-header',
  standalone: true,
  templateUrl: './header.component.html',
  imports: [
    NgIf
  ],
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit{
  accessToken: string  |undefined;
  username: string  | undefined;
  role: string | null | undefined;
  constructor(private router: Router,
              private jwtUtil: JwtUtil,
              private authService:AuthService) {
  }

  ngOnInit(): void {
      this.authService.getToken().subscribe(token => {
        this.accessToken = token;
        if (localStorage.getItem('accessToken') !== null){
          this.username = this.jwtUtil.getUsername()
          console.log(this.username)
        }
      });
      this.role = this.jwtUtil.getRole();
  }

  logout() {
    localStorage.removeItem("accessToken");
    this.goToPage('')
    location.reload();
  }
  public goToPage(pageName:string){
    this.router.navigate([`${pageName}`])
  }
}
<div class="flex justify-between h-12 align-items-center border-b-2"
  style="height: 5rem; background-color: #153362">
  <a class="font-bold text-lg mx-3 cursor-pointer text-yellow-200" (click)="goToPage('/')">Logo</a>
  <div class="w-5/12 flex align-items-center justify-evenly ">
    <div *ngIf="!accessToken  " class="flex w-4/12 justify-between">
      <button class="btn text-yellow-200 bg-indigo-500" (click)="goToPage('/signup')">Sign Up</button>
      <button class="btn text-yellow-200 bg-amber-700" (click)="goToPage('/signin')">Sign In</button>
    </div>
    <div *ngIf="accessToken">
      <button class="btn btn-danger" (click)="logout()">Log out</button>
    </div>
    <h1 class="text-white text-xl" *ngIf="username">{{username}}</h1>
    <div *ngIf="role === 'ADMIN'" class="dropdown">
      <button class="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
        Actions
      </button>
      <ul class="dropdown-menu" >
        <li><button class="dropdown-item" (click)="goToPage('admin/tournament')">Create tournament</button></li>
      </ul>
    </div>
  </div>
</div>
