import {Component, EventEmitter, Input, Output} from '@angular/core';
import {NgForOf, NgIf} from "@angular/common";


@Component({
  selector: 'app-tournament-sign-in',
  templateUrl: './tournament-sign-in.component.html',
  standalone: true,
  imports: [
    NgForOf,
    NgIf
  ],
  styleUrls: ['./tournament-sign-in.component.css']
})
export class TournamentSignInComponent {
  @Input() tournamentSignupWindowOpen: boolean = false;
  @Output() tournamentSignupWindowOpenChange = new EventEmitter<boolean>();

}
<ng-container>
  <div *ngIf="tournamentSignupWindowOpen" class="z-2 left-0 top-0 absolute w-screen h-screen flex items-center justify-center " style="background-color: rgba(100,100,100,0.4)">
    <div class="flex bg-white rounded w-2/6 h-50 flex-col">
      <i class="fa-solid fa-x text-red-700 m-4 font-bold text-4xl cursor-pointer" (click)="(tournamentSignupWindowOpen = false)"></i>
      <div class="flex-col flex justify-evenly items-center h-full">
        <div class="w-full h-20">

        </div>
      </div>
    </div>
  </div>
</ng-container>
