import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {AsyncPipe, NgForOf, NgIf} from "@angular/common";
import {TournamentSignInService} from "../../services/TournamentSigninService";
import {PersonalService} from "../../services/PersonalService";
import {TeamService} from "../../services/TeamService";
import {JwtUtil} from "../../util/JwtUtil";
import {Router} from "@angular/router";


@Component({
  selector: 'app-tournament-sign-in',
  templateUrl: './tournament-sign-in.component.html',
  standalone: true,
  imports: [
    NgForOf,
    NgIf,
    AsyncPipe
  ],
  styleUrls: ['./tournament-sign-in.component.css']
})
export class TournamentSignInComponent implements OnInit{
  tournamentSignupWindowOpen = false;
  teams$ = this.personalService.getTeamsByPersonal(this.jwtUtil.getId());
  public logoDataMap: { [key: number]: string } = {};
  public isHasLogoMap: {[key: number] : boolean} = {}


  constructor(public tournamentSignInService: TournamentSignInService,
              private personalService: PersonalService,
              private teamService: TeamService,
              private jwtUtil:JwtUtil,
              private router: Router,
              ) {

    this.tournamentSignInService.tournamentSignupWindowOpen$.subscribe((value) => {
      this.tournamentSignupWindowOpen = value;
    });
  }



  ngOnInit(): void {
        this.loadLogos()
    }

  loadLogos(): void {
    this.teams$.forEach(value => {
      value.forEach(value1 => {
        this.teamService.getTeamLogo(value1.id).subscribe(
          value2 => {
            this.logoDataMap[value1.id] = 'data:image/jpeg;base64,' + value2.data
            this.isHasLogoMap[value1.id] = true;
          }
          ,error => {
            this.isHasLogoMap[value1.id] = false
          }
        )
      })
    })

  }
  getLogo(id: number): string {
    return this.logoDataMap[id]
  }

  redirectToPage(url:string){
    this.router.navigateByUrl(url);
  }

}
<div *ngIf="tournamentSignupWindowOpen" class="z-2 left-0 top-0 absolute w-full h-screen flex items-center justify-center " style="background-color: rgba(100,100,100,0.4)">
  <ng-container *ngIf="teams$ |async as teams">
    <div class="relative flex bg-white rounded w-2/6 h-50 flex-col items-center pt-10">
      <i class="absolute fa-solid fa-x text-red-700 m-4 font-bold text-4xl cursor-pointer left-2 top-2" (click)="tournamentSignInService.setTournamentSignupWindowOpen(false)"></i>
      <p class="text-2xl">Choose team</p>
      <div class="h-full flex-col flex my-4 w-5/6 border-blue-300 border-3" >
        <div class="border-1 hover:bg-sky-900 cursor-pointer border-blue-400 w-full flex items-center justify-center bg-sky-700" *ngFor="let team of teams">
          <img class="" style="width: 50px; height: 50px" [src]="getLogo(team.id)" alt="">
          <p class="ml-5">{{team.name}}</p>
        </div>
      </div>
    </div>
  </ng-container>
</div>
