import { Component } from '@angular/core';
import {NgForOf, NgStyle} from "@angular/common";
import {Observable} from "rxjs";
import {TournamentRequestData} from "../../../../models/request/TournamentRequestData";
import {TournamentService} from "../../../../services/TournamentService";
import {ActivatedRoute, Router} from "@angular/router";

@Component({
  selector: 'app-tournament-all-list',
  templateUrl: './tournament-all-list.component.html',
  standalone: true,
  imports: [
    NgForOf,
    NgStyle
  ],
  styleUrls: ['./tournament-all-list.component.css']
})
export class TournamentAllListComponent {
  public tournaments$: Observable<TournamentRequestData[]> = this.tournamentService.getALlTournaments(null);
  public logoDataMap: { [key: number]: string } = {};
  public isHasLogoMap: {[key: number] : boolean} = {}
  selectedOption: string = 'upcoming';


  constructor(private tournamentService: TournamentService,
              private router:Router,
              private route: ActivatedRoute) {
  }

  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      const filter = params['filter'];
      this.tournaments$ = this.tournamentService.getALlTournaments(filter)
    });
    this.loadLogos()
  }


  loadLogos(): void {
    this.tournaments$.forEach(value => {
      value.forEach(value1 => {
        this.tournamentService.getTournamentLogo(value1.id).subscribe(
          value2 => {
            this.logoDataMap[value1.id] = 'data:image/jpeg;base64,' + value2.data
            this.isHasLogoMap[value1.id] = true;
          }
          ,error => {
            this.isHasLogoMap[value1.id] = false
          }
        )
      })
    })

  }

  redirectToPage(url: string) {
    this.router.navigateByUrl(url)
  }

  getLogo(id: number): string {
    return this.logoDataMap[id]
  }

  toLocalDate(data: Date) {
    return new Date(data).toLocaleString()
  }


  handleSelection() {
    this.router.navigateByUrl('tournaments?filter=' + this.selectedOption)
  }
}
<div class="mt-5 grid mx-5 xl:grid-cols-3 md:grid-cols-2 sm:grid-cols-1 gap-10" [ngStyle] ="{'min-height' : 'calc(300px * '+tournaments.length/3}">
  <div (click)="redirectToPage('tournaments/' + tournament.id)" class="tournament_cont flex flex-col h-3/4 rounded-xl hover:shadow-xl hover:shadow-blue-300 hover:border" *ngFor="let tournament of tournaments">
    <div class="img_cont w-full rounded-t-xl " style="background-position: center; height: 25vh" [class.noLogo] ="!isHasLogoMap[tournament.id]" [ngStyle]="{'background-image': 'url(' + getLogo(tournament.id) + ')'}"></div>
    <div class="w-full bg-sky-900 rounded-b-xl relative flex-col flex justify-between" style="height: 20vh">
      <div class="flex flex-row mx-3 justify-between mt-2">
        <p class="text-2xl text-white">{{tournament.discipline + ' - ' + tournament.name }}</p>
        <div class="circle-container">
          <div class="fill" [ngStyle]="{'background': 'conic-gradient(rgb(21, 205, 57) ' + (tournament.currTeams/(tournament.maxTeams/100)) + '%, transparent 0)'}"></div>
          <div class="absolute bg-sky-800 inner_fill"></div>
        </div>
      </div>
      <div class="mb-2 flex flex-row mx-3 justify-between text-lg text-white">
        <p class="">{{toLocalDate(tournament.startDate)}}</p>
        <p >{{tournament.currTeams + ' - ' + tournament.maxTeams}}</p>
      </div>

    </div>
  </div>
</div>
