import {Component, OnInit} from '@angular/core';
import {AllTournamentRequestData} from "../../../../models/request/AllTournamentRequestData";
import {TournamentService} from "../../../../services/TournamentService";
import {AsyncPipe, NgForOf, NgIf, NgStyle} from "@angular/common";
import {Observable} from "rxjs";
import {TournamentRequestData} from "../../../../models/request/TournamentRequestData";

@Component({
  selector: 'app-tournaments-all',
  templateUrl: './tournaments-all.component.html',
  standalone: true,
  imports: [
    NgForOf,
    AsyncPipe,
    NgIf,
    NgStyle
  ],
  styleUrls: ['./tournaments-all.component.css']
})
export class TournamentsAllComponent implements OnInit{

  public tournaments$: Observable<TournamentRequestData[]> = this.tournamentService.getALlTournaments();
  public logoDataMap: { [key: number]: string } = {}; // Объявите объект для хранения данных логотипов
  constructor(private tournamentService: TournamentService) {
  }

  ngOnInit(): void {

  }


  loadLogos(tournaments: any[]): void {
    const promises = tournaments.map(tournament => this.getLogo(tournament.id));
    Promise.all(promises).then(logos => {
      tournaments.forEach((tournament, index) => {
        this.logoDataMap[tournament.id] = logos[index];
      });
    });
  }


  async getLogo(id: number): Promise<string> {
    try {
      const res = await this.tournamentService.getTournamentLogo(id).toPromise();
      if (res) {
        return 'data:image/jpeg;base64,' + res.data;
      }
      throw new Error('Response is undefined');
    } catch (error) {
      // Обработка ошибки
      throw new Error('Failed to fetch logo');
    }
  }



}
<ng-container *ngIf="tournaments$ | async as tournaments">
  <div class="flex flex-col w-screen" style="min-height: calc(100vh - 5rem);
                                             background: rgb(2,0,36);
                                             background: linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(3,56,91,1) 81%);">
    <div class="flex mt-4" style="margin-left: 15%">
      <i class="fa-solid fa-trophy text-5xl text-cyan-500" style="margin-right: 5%"></i>
      <p class="text-4xl text-blue-200 font-bold">Tournaments</p>
    </div>
    <div class="flex justify-center">
      <select>
        <option selected class="form-select">Upcoming</option>
      </select>
    </div>
    <div class="grid grid-cols-3">
      <div class="flex w-3/5 h-3/5" *ngFor="let tournament of tournaments">
        <div class="w-3/5 h-3/5" [ngStyle]="{'background-image': 'url(' + logoDataMap[tournament.id] + ')'}"></div>
      </div>
    </div>
  </div>
</ng-container>
