import {Component, OnDestroy, OnInit} from '@angular/core';
import {TournamentService} from "../../../../services/TournamentService";
import {ActivatedRoute, Router} from "@angular/router";
import {TournamentRequestData} from "../../../../models/request/TournamentRequestData";
import {DatePipe, LowerCasePipe, NgClass, NgIf, NgStyle} from "@angular/common";
import {FormsModule} from "@angular/forms";
import {JwtUtil} from "../../../../util/JwtUtil";
import {interval, Subscription} from "rxjs";
import {error} from "@angular/compiler-cli/src/transformers/util";

@Component({
  selector: 'app-tournament',
  standalone: true,
  templateUrl: './tournament.component.html',
  imports: [
    NgIf,
    NgClass,
    FormsModule,
    NgStyle,
    DatePipe,
    LowerCasePipe
  ],
  styleUrls: ['./tournament.component.css']
})
export class TournamentComponent implements OnInit,OnDestroy{

  public infoLoaded: boolean = false;
  public tournamentId : number | undefined ;
  public tournamentRequestData: TournamentRequestData | undefined;
  public imageWindowOpen: boolean = false;
  // @ts-ignore
  public uploadImage :File | undefined;
  public isHasImage: boolean = false;
  public dbImage: string | undefined;
  public role: string | null;
  public tournamentStart: Date | undefined;
  public timeUntilStart: number | undefined;
  private subscription: Subscription = new Subscription();
  public isStarted: boolean = false;
  public startDateString : string = '';
  public deleteWindowOpen: boolean = false;
  public cancelWindowOpen: boolean = false;

  constructor(private tournamentService:TournamentService,
              private route: ActivatedRoute,
              private router: Router,
              private jwtUtil: JwtUtil
              ) {
    this.route.params.subscribe(value => {
      this.tournamentId = value['id']
    })
    this.role = this.jwtUtil.getRole();
  }


  ngOnInit(): void {
    this.tournamentService.getTournament(this.tournamentId).subscribe(data => {
      this.tournamentRequestData = data;
      this.infoLoaded = true;
      this.isStarted = data.isStarted;
      this.startDateString = new Date(data.startDate).toLocaleString()
      console.log(this.startDateString)
      console.log(this.tournamentRequestData)
      this.getTimeToStartTournament(data);
    },
    error => {
      if (error.error == "Tournament not found") {
        this.router.navigateByUrl('error/404')
      }
    });
    this.tournamentService.getTournamentLogo(this.tournamentId).subscribe( res =>{
      this.dbImage = 'data:image/jpeg;base64,' + res.data;
      this.isHasImage = true;
      }
    )

  }

  public onImageUpload(event:Event) {
    if (event && event.target){
        // @ts-ignore
        this.uploadImage = event.target.files[0];
      }
  }
  public submitImage() {
    this.tournamentService.setTournamentImage(this.uploadImage, this.tournamentId).subscribe()
    location.reload();
  }

  public goToPage(pageName: string) {
    this.router.navigate([`${pageName}`])
  }

  private getTimeToStartTournament(data: TournamentRequestData) {
    if (data && data.startDate) {
      this.tournamentStart = new Date(data.startDate);
      this.subscription = interval(1000).subscribe(x => {
        const currentTime = new Date().getTime();
        if (this.tournamentStart) {
          this.timeUntilStart = this.tournamentStart.getTime() - currentTime;
          if (this.timeUntilStart <= 0 && !this.isStarted) {
            location.reload();
          }
        }
      });
    }
  }

  public deleteTournament() {
    this.tournamentService.deleteTournament(this.tournamentId).subscribe(() =>
      this.router.navigateByUrl('tournaments')
    )
  }

  public cancelTournament() {
    this.tournamentService.cancelTournament(this.tournamentId).subscribe(() =>
    location.reload()
    ,error => console.log(error.error))
  }

  ngOnDestroy(): void {
    if (this.subscription)
    this.subscription.unsubscribe();
  }

}
<ng-container >
  <div class="flex flex-col " style="min-height: calc(100vh - 5rem);">
    <div class="w-full h-80 flex flex-col justify-between" [class.no_background]="!isHasImage" style="background-size: cover" [ngStyle]="{'background-image': 'url(' + dbImage + ')'}">

      <div class="flex justify-between ">
        <span class="flex mt-2">
          <a class="mr-2 ml-3 cursor-pointer font-semibold text-lg text-gray-700 hover:text-amber-100" (click)="goToPage('tournaments')">Tournaments</a>
          <p class="mr-2 text-amber-100 font-semibold text-lg">></p>
          <p class="text-amber-100 font-semibold text-lg">{{tournamentRequestData?.name}}</p>
        </span>
          <i  *ngIf="role == 'ADMIN'" (click)="imageWindowOpen = true" class="cursor-pointer text-gray-700 border-3 border-black flex  justify-center rounded-3xl w-12 h-12 mt-3 mr-10 font-bold text-4xl fa-solid fa-plus"></i>
      </div>
      <div class="flex flex-col justify-center items-center">
        <h1 class="text-amber-100 text-4xl mb-1 w-5/6 ">{{tournamentRequestData?.discipline}} - {{tournamentRequestData?.name}}</h1>
      </div>
    </div>
    <main class=" h-full bg-sky-950">
      <div class="ml-3 mt-3  h-2/5 flex flex-col ">
        <div class="text-amber-100 text-2xl flex self-center justify-between w-2/5" *ngIf="!isStarted">
          <p>Sign up closes in {{ timeUntilStart | date: 'HH:mm:ss' : 'UTC' }}</p>
          <button class="btn btn-success w-52">Sign up</button>
        </div>
      </div>
      <div class="w-full flex flex-col" style="height: 40vh">
        <div class="h-5 w-5/6 border-b-2 border-blue-300 flex self-center"></div>
        <div class="flex justify-between self-center h-100 w-5/6">
          <div class="flex-col text-xl justify-evenly flex">
            <span>
              <p class="text-blue-500 ">GAME</p>
              <p class="text-blue-200">{{tournamentRequestData?.discipline}}</p>
            </span>
            <span>
              <p class="text-blue-500 ">TYPE</p>
              <p class="text-blue-200">{{tournamentRequestData?.tournamentType | lowercase}}</p>
            </span>
          </div>
          <div class="flex-col text-xl justify-evenly flex">
            <span>
              <p class="text-blue-500 ">STARTS </p>
              <p class="text-blue-200">{{startDateString}}</p>
            </span>
            <span>
              <p class="text-blue-500 ">TEAMS TO START</p>
              <p class="text-blue-200">{{tournamentRequestData?.minTeams}}</p>
            </span>
          </div>
          <div class="flex-col flex w-1/4 justify-center">
            <div class="h-1/4 text-blue-300 bg-sky-700 flex justify-center items-center text-2xl border-b-2 border-b-cyan-900">
              <p>Available slots</p>
            </div>
            <div class="h-1/4 text-blue-300 bg-sky-800 flex justify-center items-center text-2xl">
              <p>{{tournamentRequestData?.currTeams}}/{{tournamentRequestData?.maxTeams}}</p>
            </div>

          </div>
        </div>
      </div>
    </main>

  </div>
  <i *ngIf="role == 'ADMIN'" class="fa-solid fa-ban absolute left-5 text-red-700 text-2xl cursor-pointer" style="top: 70%" (click)="cancelWindowOpen = true"></i>
  <div *ngIf="cancelWindowOpen" class="left-0 top-0 absolute w-screen h-screen flex items-center justify-center " style="background-color: rgba(100,100,100,0.4)">
    <div class="flex bg-white rounded w-2/5 h-50 flex-col">
      <i class="fa-solid fa-x text-red-700 m-4 font-bold text-4xl cursor-pointer" (click)="(cancelWindowOpen = false)"></i>
      <div class="flex-col flex justify-evenly items-center h-full">
        <h1 class="text-center text-2xl">Are you sure you want to cancel the "{{tournamentRequestData?.name}}" tournament?</h1>
        <button class="btn btn-danger" (click)="cancelTournament()">Cancel</button>
      </div>
    </div>
  </div>
  <i *ngIf="role == 'ADMIN'" class="fa-solid fa-trash absolute left-5 top-3/4 text-red-700 text-2xl cursor-pointer" (click)="deleteWindowOpen = true"></i>
  <div *ngIf="deleteWindowOpen" class="left-0 top-0 absolute w-screen h-screen flex items-center justify-center " style="background-color: rgba(100,100,100,0.4)">
    <div class="flex bg-white rounded w-2/5 h-50 flex-col">
      <i class="fa-solid fa-x text-red-700 m-4 font-bold text-4xl cursor-pointer" (click)="(deleteWindowOpen = false)"></i>
      <div class="flex-col flex justify-evenly items-center h-full">
        <h1 class="text-center text-2xl">Are you sure you want to delete the "{{tournamentRequestData?.name}}" tournament?</h1>
        <button class="btn btn-danger" (click)="deleteTournament()">Delete</button>
      </div>
    </div>
  </div>
  <div *ngIf="imageWindowOpen" class="left-0 top-0 absolute w-screen h-screen flex items-center justify-center " style="background-color: rgba(100,100,100,0.4)">
    <div class="flex bg-white rounded w-2/5 h-50 flex-col">
      <i class="fa-solid fa-x text-red-700 m-4 font-bold text-4xl cursor-pointer" (click)="(imageWindowOpen = false); (uploadImage = undefined)"></i>
      <form (ngSubmit)="submitImage()" class="flex flex-col w-4/5 items-center mt-5 self-center">
        <input (change)="onImageUpload($event)" class="form-control border-blue-300" type="file">
        <button [disabled]="uploadImage == undefined" type="submit" class="btn btn-primary bg-sky-700 mt-10" >Set image</button>
      </form>
    </div>
  </div>
</ng-container>
