import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-tournament-creation',
  templateUrl: './tournament-creation.component.html',
  styleUrls: ['./tournament-creation.component.css']
})
export class TournamentCreationComponent implements OnInit {
  public maxMin: boolean = true;

  constructor(private fb: FormBuilder) {}

  ngOnInit(): void {
    this.form.valueChanges.subscribe(value => {
      this.maxMin = this.maxMinCheck(value['maxTeams'], value['minTeams']);
      console.log(this.form.invalid, this.form.errors);
    });
    // TODO: form fields check and request to the server
  }

  public form: FormGroup = this.fb.group({
    name: new FormControl(null, [Validators.required]),
    discipline: new FormControl('Dota 2', Validators.required),
    maxTeams: new FormControl(null, [Validators.required, Validators.min(2), Validators.max(128)]),
    minTeams: new FormControl(null, [Validators.required, Validators.min(2), Validators.max(128)]),
    startDate: new FormControl(null, [Validators.required]),
    tournamentType: new FormControl('SINGLE_ELIMINATION', Validators.required),
  });

  private maxMinCheck(max: number, min: number): boolean {
    if (max != null && min != null) return max >= min;
    return true;
  }
}
<ng-container>
  <div class="flex flex-col items-center w-full bg-sky-950 justify-center" style="height: calc(100vh - 5rem)">
    <form [formGroup]="form"  class="flex flex-col bg-white rounded w-4/12 p-4">
      <div class="mb-3">
        <label for="name" class="form-label">Tournament name</label>
        <input formControlName="name" max="50" type="text" class="border-blue-300 form-control" id="name" aria-describedby="name">
      </div>
      <div class="mb-3">
        <label for="discipline" class="form-label">Tournament discipline</label>
        <select id="discipline" formControlName="discipline" class="form-select border-blue-300" aria-label="Discipline">
          <option [value]="'Dota 2'" [selected]="form.value['discipline'] === 'Dota 2'">Dota 2</option>
          <option [value]="'CS 2'" [selected]="form.value['discipline'] === 'CS 2'">CS 2</option>
          <option [value]="'Apex'" [selected]="form.value['discipline'] === 'Apex'">Apex</option>
        </select>
      </div>

      <div class="mb-3">
        <label for="minTeams" class="form-label">Minimum teams in tournament</label>
        <input formControlName="minTeams" type="number" class="border-blue-300 form-control" id="minTeams" aria-describedby="maxTeams">
      </div>
      <div class="mb-3">
        <label for="maxTeams" class="form-label">Maximum teams in tournament</label>
        <input formControlName="maxTeams" min="2" type="number" class="border-blue-300 form-control" id="maxTeams" aria-describedby="maxTeams">
        <p class="text-danger" *ngIf="!maxMin">*Maximum teams must be equal or bigger then minimum</p>
      </div>
      <div class="mb-3">
        <label for="startDate" class="form-label">Start date</label>
        <input formControlName="startDate" type="datetime-local" class="border-blue-300 form-control" id="startDate" aria-describedby="maxTeams">
      </div>
      <div class="mb-3">
        <label for="tournamentType" class="form-label">Tournament type</label>
        <select id="tournamentType" formControlName="discipline" class="form-select border-blue-300"  aria-label="tournamentType">
          <option [value]="'SINGLE_ELIMINATION'" [selected]="form.get('tournamentType')?.value === 'SINGLE_ELIMINATION'">{{form.get('tournamentType')?.value}}</option>
          <option [value]="'DOUBLE_ELIMINATION'">Double Elimination</option>
          <option [value]="'Round Robin'">Round robin</option>
        </select>

      </div>
      <button type="submit" class="btn btn-primary bg-sky-700" [disabled]="form.invalid ">Submit</button>
    </form>
  </div>
</ng-container>
