import {Component, OnDestroy, OnInit} from '@angular/core';
import {TournamentService} from "../../../services/TournamentService";
import {ActivatedRoute, Router} from "@angular/router";
import {TournamentRequestData} from "../../../models/request/TournamentRequestData";
import {DatePipe, NgClass, NgIf, NgStyle} from "@angular/common";
import {FormsModule} from "@angular/forms";
import {JwtUtil} from "../../../util/JwtUtil";
import {interval, Subscription} from "rxjs";

@Component({
  selector: 'app-tournament',
  standalone: true,
  templateUrl: './tournament.component.html',
  imports: [
    NgIf,
    NgClass,
    FormsModule,
    NgStyle,
    DatePipe
  ],
  styleUrls: ['./tournament.component.css']
})
export class TournamentComponent implements OnInit,OnDestroy{

  public infoLoaded: boolean = false;
  public tournamentId : number | undefined ;
  public tournamentRequestData: TournamentRequestData | undefined;
  public imageWindowOpen: boolean = false;
  // @ts-ignore
  public uploadImage :File | undefined;
  public isHasImage: boolean = false;
  public dbImage: string | undefined;
  public role: string | null;
  public tournamentStart: Date | undefined;
  public timeUntilStart: number | undefined;
  private subscription: Subscription = new Subscription();

  constructor(private tournamentService:TournamentService,
              private route: ActivatedRoute,
              private router: Router,
              private jwtUtil: JwtUtil
              ) {
    this.route.params.subscribe(value => {
      this.tournamentId = value['id']
    })
    this.role = this.jwtUtil.getRole();
  }


  ngOnInit(): void {
    this.tournamentService.getTournament(this.tournamentId).subscribe(data => {
        this.tournamentRequestData = data;
        this.infoLoaded = true;
        console.log(this.tournamentRequestData)
        this.getTimeToStartTournament(data);
      },
      error => {
        if (error.errorMessage == "Tournament not found") {
          this.router.navigateByUrl('error/404')
        }
      }
    );
    this.tournamentService.getTournamentLogo(this.tournamentId).subscribe( res =>{
      this.dbImage = 'data:image/jpeg;base64,' + res.data;
      this.isHasImage = true;
      }
    )

  }

  public onImageUpload(event:Event) {
    if (event && event.target){
        // @ts-ignore
        this.uploadImage = event.target.files[0];
      }
  }
  public submitImage() {
    this.tournamentService.setTournamentImage(this.uploadImage, this.tournamentId).subscribe()
    location.reload();
  }

  public goToPage(pageName: string) {
    this.router.navigate([`${pageName}`])
  }

  private getTimeToStartTournament(data: TournamentRequestData) {
    if (data && data.startDate) {
      this.tournamentStart = new Date(data.startDate);
      this.subscription = interval(1000).subscribe(x => {
        const currentTime = new Date().getTime();
        if (this.tournamentStart) {
          this.timeUntilStart = this.tournamentStart.getTime() - currentTime;
          console.log(this.timeUntilStart);
        }
      });
    }
  }


  ngOnDestroy(): void {
    if (this.subscription)
    this.subscription.unsubscribe();
  }

}
<ng-container >
  <div class="w-screen  flex flex-col " style="min-height: calc(100vh - 5rem)">
    <div class="w-full h-60 flex flex-col justify-between" [class.no_background]="!isHasImage" style="background-size: cover" [ngStyle]="{'background-image': 'url(' + dbImage + ')'}">

      <div class="flex w-screen justify-between ">
        <span class="flex mt-2"><a class="mr-2 ml-3 cursor-pointer font-semibold text-lg text-gray-700 hover:text-amber-100" (click)="goToPage('tournaments')">Tournaments</a>
        <p class="mr-2 text-amber-100 font-semibold text-lg">></p>
        <p class="text-amber-100 font-semibold text-lg">{{tournamentRequestData?.name}}</p>
        </span>
          <i  *ngIf="role == 'ADMIN'" (click)="imageWindowOpen = true" class="cursor-pointer text-gray-400 border-3 border-black flex  justify-center rounded-3xl w-12 h-12 mt-3 mr-10 font-bold text-4xl fa-solid fa-plus"></i>
      </div>
      <div class="text-white">
        Время до начала турнира: {{ timeUntilStart | date: 'HH:mm:ss' : 'UTC' }}
      </div>
      <div>
        <h1 class="text-amber-100 text-4xl mb-1 ml-3">{{tournamentRequestData?.discipline}} - {{tournamentRequestData?.name}}</h1>
      </div>
    </div>
  </div>

  <div *ngIf="imageWindowOpen" class="left-0 top-0 absolute w-screen h-screen flex items-center justify-center " style="background-color: rgba(100,100,100,0.4)">
    <div class="flex bg-white rounded w-2/5 h-50 flex-col">
      <i class="fa-solid fa-x text-red-700 m-4 font-bold text-4xl cursor-pointer" (click)="(imageWindowOpen = false); (uploadImage = undefined)"></i>
      <form (ngSubmit)="submitImage()" class="flex flex-col w-4/5 items-center mt-5 self-center">
        <input (change)="onImageUpload($event)" class="form-control border-blue-300" type="file">
        <button [disabled]="uploadImage == undefined" type="submit" class="btn btn-primary bg-sky-700 mt-10" >Set image</button>
      </form>
    </div>
  </div>
</ng-container>
