import {Component, Input, OnInit} from '@angular/core';
import {BracketService} from "../../../../../services/BracketService";
import {BracketData} from "../../../../../models/request/BracketData";
import {NgForOf, NgIf, NgStyle} from "@angular/common";
import {StageData} from "../../../../../models/request/StageData";
import {last} from "rxjs";
import {JwtUtil} from "../../../../../util/JwtUtil";
import {TeamService} from "../../../../../services/TeamService";
import {TeamData} from "../../../../../models/request/TeamData";
import {Router} from "@angular/router";

@Component({
  selector: 'app-bracket',
  standalone: true,
  templateUrl: './bracket.component.html',
  imports: [
    NgIf,
    NgForOf,
    NgStyle
  ],
  styleUrls: ['./bracket.component.css']
})
export class BracketComponent implements OnInit{
  @Input() bracketId: number | undefined;
  @Input() adminId: number | undefined;
  @Input() winnerId: number | undefined;

  public bracket: BracketData | undefined;
  public role: string | null = this.jwtUtil.getRole();
  public userId: number | undefined = this.jwtUtil.getId();
  public winner: TeamData | undefined;

  public logoDataMap: { [key: number]: string } = {};
  public isHasLogoMap: { [key: number]: boolean } = {}
  public teams: TeamData[] | undefined;
  constructor(private bracketService: BracketService,
              private jwtUtil: JwtUtil,
              private teamService: TeamService,
              private router: Router
              ) {
  }
  ngOnInit(): void {
    this.bracketService.findBracket(this.bracketId).subscribe(value => {
      this.bracket = value;
      this.teams = [];

      value.stages.forEach(stage => {
        stage.matches.forEach(match => {
          this.teams?.push(match.team1);
          this.teams?.push(match.team2);
        });
      });

      this.loadLogos();
    });

    if (this.winnerId != 0) {
      this.teamService.findById(this.winnerId).subscribe(value => {
        this.winner = value;
      });
    }
  }



  loadLogos(): void {
    if (this.teams)
    this.teams.forEach(value => {
        this.teamService.getTeamLogo(value.id).subscribe(
          value2 => {
            this.logoDataMap[value.id] = 'data:image/jpeg;base64,' + value2.data
            this.isHasLogoMap[value.id] = true;
          }
          , error => {
            this.isHasLogoMap[value.id] = false
          }
        )
      })
  }

  getLogo(id: number): string {
    return this.logoDataMap[id]
  }

  teamWon(teamId: number, bracketId: number, matchId: number | undefined, stageId: number) {
    this.bracketService.teamWon(teamId,matchId,bracketId,stageId).subscribe(
      () => {
        location.reload();
      }
    );
  }

  navigateToTeamPage(id: number | undefined) {
    this.router.navigateByUrl('teams/' + id);
  }
}
<ng-container *ngIf="bracket">
  <div style="height: 75vh" class="max-w-5/6 w-5/6 overflow-x-scroll overflow-y-scroll">
    <svg [attr.width]="30 * (bracket.stages.length + 1) + 'vw'" [attr.height]="5 * (bracket.stages[0].matches.length) + 'rem'" class="self-center border-2 border-blue-700" style="min-height: 75vh; min-width: 90vw">
      <!-- Линии связи между матчами -->
      <ng-container *ngFor="let stage of bracket.stages">
        <ng-container *ngFor="let match of stage.matches">
          <ng-container *ngIf="match.team1 && match.team2">
            <!-- Начальная точка линии -->
            <line [attr.x1]="getXPosition(stage.stageId)" [attr.y1]="getYPosition(match.matchId, match.team1.id)" [attr.x2]="getXPosition(stage.stageId + 1)" [attr.y2]="getYPosition(match.matchId, match.team2.id)" stroke="black" stroke-width="2" />
          </ng-container>
        </ng-container>
      </ng-container>

      <!-- Ваши блоки с матчами и командами -->
      <ng-container *ngFor="let stage of bracket.stages">
        <div class="flex flex-col items-center justify-around" style="min-height: 75vh; width: 30vw">
          <div class="flex flex-col w-5/6 h-24 bg-sky-400" *ngFor="let match of stage.matches">
            <!-- Блок команды 1 -->
            <div class="flex flex-col w-full h-50 relative cursor-pointer hover:bg-sky-600" (click)="navigateToTeamPage(match.team1.id)" *ngIf="match.team1">
              <!-- ... -->
            </div>

            <!-- Пустой блок -->
            <div class="flex flex-col w-full h-50 relative" *ngIf="!match.team1"></div>

            <!-- Горизонтальная полоса сверху -->
            <div class="border-b border-gray-500"></div>

            <!-- Блок команды 2 -->
            <div class="flex flex-col w-full h-50 relative cursor-pointer hover:bg-sky-600" (click)="navigateToTeamPage(match.team2.id)" *ngIf="match.team2">
              <!-- ... -->
            </div>

            <!-- Пустой блок -->
            <div class="flex flex-col w-full h-50 relative" *ngIf="!match.team2"></div>
          </div>
        </div>
      </ng-container>

    </svg>
  </div>
</ng-container>
