import {Component, Input, OnInit} from '@angular/core';
import {BracketService} from "../../../../../services/BracketService";
import {BracketData} from "../../../../../models/request/BracketData";
import {NgForOf, NgIf} from "@angular/common";
import {StageData} from "../../../../../models/request/StageData";
import {last} from "rxjs";

@Component({
  selector: 'app-bracket',
  standalone: true,
  templateUrl: './bracket.component.html',
  imports: [
    NgIf,
    NgForOf
  ],
  styleUrls: ['./bracket.component.css']
})
export class BracketComponent implements OnInit{
  @Input() bracketId: number | undefined;

  public bracket: BracketData | undefined;

  constructor(private bracketService: BracketService,
              ) {
  }
  ngOnInit(): void {
      this.bracketService.findBracket(this.bracketId).subscribe(value => {
        this.bracket = value
      })
  }


  protected readonly last = last;
}
<ng-container *ngIf="bracket">
  <div class="max-w-5/6 w-5/6 flex justify-center items-center border-2 border-blue-700 overflow-x-scroll overflow-y-scroll" style="max-height: 75vh; min-height: 75vh">
    <div class="flex flex-col w-2/5 items-center justify-center" style="min-height: 75vh" *ngFor="let stage of bracket.stages">
      <div class="flex flex-col w-80 h-3/5 bg-sky-400 " *ngFor="let match of stage.matches">
        <div class="flex flex-col w-full h-50 " *ngFor="let team of match.teams; let last = last">
          <p>{{team.name}}</p>
          <div *ngIf="!last" class="border-b border-gray-500"></div>
        </div>
      </div>
    </div>
    <div class="flex flex-col w-2/5 items-center justify-center" style="min-height: 75vh" >
      <div class="flex flex-col w-80 h-3/5 bg-sky-400">
        <div class="flex flex-col w-full h-50">
          <p>Winner</p>
        </div>
      </div>
    </div>
  </div>
</ng-container>
