import {Component, OnInit} from '@angular/core';
import {Router} from "@angular/router";
import {NgIf} from "@angular/common";
import {JwtUtil} from "../../util/JwtUtil";
import {HeaderDataService} from "../../services/HeaderDataService";

@Component({
  selector: 'app-header',
  standalone: true,
  templateUrl: './header.component.html',
  imports: [
    NgIf
  ],
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit{
  accessToken: string | null |undefined;
  username: string | null | undefined;
  constructor(private router: Router,
              private jwtUtil: JwtUtil,
              private headerDataService:HeaderDataService) {
  }

  ngOnInit(): void {
    this.headerDataService.currentAccessToken.subscribe(token => {
      this.accessToken = token;
    });
    if (this.accessToken) {
      this.username = this.jwtUtil.getUsername()
      console.log(this.username)
    }
  }

  logout() {
    localStorage.removeItem("accessToken");
    this.headerDataService.changeAccessToken(null)
    this.goToPage('')
  }
  public goToPage(pageName:string){
    this.router.navigate([`${pageName}`])
  }
}
<div class="flex justify-between h-12 align-items-center border-b-2"
  style="height: 5rem; background-color: #153362">
  <a class="font-bold text-lg mx-3 cursor-pointer text-yellow-200" (click)="goToPage('/')">Logo</a>
  <div class="w-5/12 flex align-items-center ">
    <div *ngIf="!accessToken" class="flex w-4/12 justify-between">
      <button class="btn text-yellow-200 bg-indigo-500" (click)="goToPage('/signup')">Sign Up</button>
      <button class="btn text-yellow-200 bg-amber-700" (click)="goToPage('/signin')">Sign In</button>
    </div>
    <div *ngIf="accessToken">
      <button class="btn btn-danger" (click)="logout()">Log out</button>
    </div>


  </div>
</div>
