import { Component } from '@angular/core';
import {NgIf} from "@angular/common";
import {FormBuilder, FormControl, FormGroup, ReactiveFormsModule, Validators} from "@angular/forms";

@Component({
  selector: 'app-tournament-creation',
  standalone: true,
  templateUrl: './tournament-creation.component.html',
  imports: [
    NgIf,
    ReactiveFormsModule
  ],
  styleUrls: ['./tournament-creation.component.css']
})
export class TournamentCreationComponent {

  constructor(private fb: FormBuilder,
              ) {
  }

  public form: FormGroup = this.fb.group({
    name: new FormControl(null, [Validators.required,]),
    discipline: new FormControl(null, [Validators.required]),
    maxTeams: new FormControl(null, [Validators.required]),
    minTeamsToStart: new FormControl(null, [Validators.required]),
    creationDate: new FormControl(null, [Validators.required]),
    startDate: new FormControl(null, [Validators.required]),
    adminId: new FormControl(null, [Validators.required]),
  });
}
<ng-container>
  <div class="flex flex-col items-center w-full bg-sky-950 justify-center" style="height: calc(100vh - 5rem)">
    <form [formGroup]="form" (ngSubmit)="onSubmit()" class="flex flex-col bg-white rounded w-4/12 p-4">
      <div class="mb-3">
        <label for="inputEmail" class="form-label">Email address</label>
        <input formControlName="email" type="email" class="border-blue-300 form-control" id="inputEmail" aria-describedby="emailHelp">
        <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
        <p class="text-danger" *ngIf="errorMessage === 'User not found'">*Incorrect email</p>
      </div>
      <div class="mb-3">
        <label for="inputPassword1" class="form-label">Password</label>
        <input formControlName="password" type="password" class="border-blue-300  form-control" id="inputPassword1">
        <p class="text-danger" *ngIf="errorMessage === 'Incorrect password'">*Incorrect password</p>
      </div>
      <button type="submit" class="btn btn-primary bg-sky-700" [disabled]="form.invalid ">Submit</button>
    </form>
  </div>
</ng-container>
