import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from "@angular/common/http";
import { map, switchMap } from "rxjs/operators";
import { Observable } from "rxjs";
import { ResponseData } from "../models/ResponseData";
import { TournamentRequestData } from "../models/request/TournamentRequestData";

@Injectable({
  providedIn: 'root'
})
export class TournamentService {

  constructor(private http: HttpClient) {}

  public create(formValues: any): Observable<number> {
    const headers = new HttpHeaders().set("Authorization", "Bearer " + localStorage.getItem("accessToken"));

    const name: string = formValues['name'];
    const discipline: string = formValues['discipline'];
    const maxTeams: number = formValues['maxTeams'];
    const minTeams: number = formValues['minTeams'];
    const startDate: Date = formValues['startDate'];
    const tournamentType: string = formValues['tournamentType'];

    // Assuming 'image' is a File object
    const image: File = formValues['image'];

    return this.convertImageToByteArray(image).pipe(
      switchMap(byteArray => {
        const formData = new FormData();
        formData.append('name', name);
        formData.append('discipline', discipline);
        formData.append('maxTeams', maxTeams.toString());
        formData.append('minTeams', minTeams.toString());
        formData.append('startDate', startDate.toISOString());
        formData.append('tournamentType', tournamentType);
        formData.append('image', new Blob([byteArray]));

        return this.http.post<ResponseData<number>>(
          'http://localhost:8080/api/private/admin/tournament',
          formData,
          { headers: headers }
        ).pipe(map(res => res.data));
      })
    );
  }

  private convertImageToByteArray(file: File): Observable<Uint8Array> {
    return new Observable<Uint8Array>(observer => {
      const reader = new FileReader();
      reader.readAsArrayBuffer(file);
      reader.onload = () => {
        const arrayBuffer = reader.result as ArrayBuffer;
        const uintArray = new Uint8Array(arrayBuffer);
        observer.next(uintArray);
        observer.complete();
      };
      reader.onerror = error => {
        observer.error(error);
      };
    });
  }

  public getTournament(id: number | undefined): Observable<TournamentRequestData> {
    return this.http.get<ResponseData<TournamentRequestData>>('http://localhost:8080/api/open/tournament/' + id)
      .pipe(
        map(res => res.data)
      );
  }
}
