import {Component, OnInit} from '@angular/core';
import {TournamentService} from "../../../services/TournamentService";
import {ActivatedRoute, Router} from "@angular/router";
import {TournamentRequestData} from "../../../models/request/TournamentRequestData";
import {NgClass, NgIf} from "@angular/common";

@Component({
  selector: 'app-tournament',
  standalone: true,
  templateUrl: './tournament.component.html',
  imports: [
    NgIf,
    NgClass
  ],
  styleUrls: ['./tournament.component.css']
})
export class TournamentComponent implements OnInit{

  public infoLoaded: boolean = false;
  public tournamentId : number | undefined ;
  public tournamentRequestData: TournamentRequestData | undefined;
  constructor(private tournamentService:TournamentService,
              private route: ActivatedRoute,
              private router: Router
              ) {
    this.route.params.subscribe(value => {
      this.tournamentId = value['id']
    })
  }


  public tournamentInfoLoaded: boolean = false;
  ngOnInit(): void {
    this.tournamentService.getTournament(this.tournamentId).subscribe(data => {
        this.tournamentRequestData = data;
        this.infoLoaded = true;
        console.log(this.tournamentRequestData)
      },
      error => {
        if (error.errorMessage == "Tournament not found") {
          this.router.navigateByUrl('error/404')
        }
      }
    );
  }

  public goToPage(pageName: string) {
    this.router.navigate([`${pageName}`])
  }

}
<ng-container >
  <div class="w-screen  flex flex-col " style="min-height: calc(100vh - 5rem)">
    <div class="w-full h-60"  [class.no_background]="!tournamentRequestData?.image">
      <span class="flex w-max ">
        <a class="mr-2 font-semibold text-lg text-gray-700 hover:text-amber-100" (click)="goToPage('tournaments')">Tournaments</a>
        <p class="mr-2 font-semibold text-lg">></p>
        <p class="text-amber-100 font-semibold text-lg">{{tournamentRequestData?.name}}</p>
        <div class="self-end  left-auto top-4">
          <i class="fa-solid fa-plus"></i>
        </div>
      </span>

    </div>

  </div>
</ng-container>
