import {Component, OnInit} from '@angular/core';
import {FormBuilder, FormControl, FormGroup, ReactiveFormsModule, Validators} from "@angular/forms";
import {Router} from "@angular/router";
import {TokenService} from "../../../../services/TokenService";
import {Location, NgIf} from "@angular/common";
import {AuthService} from "../../../../services/AuthService";
import {JwtUtil} from "../../../../util/JwtUtil";

@Component({
  selector: 'app-signin',
  templateUrl: './signin.component.html',
  standalone: true,
  imports: [
    ReactiveFormsModule,
    NgIf
  ],
  styleUrls: ['./signin.component.css']
})
export class SigninComponent implements OnInit{
  constructor(private fb: FormBuilder,
              private service: AuthService,
              private router: Router,
              private authService: TokenService,
              private location: Location,
              private jwtUtil:JwtUtil
              ) {
  }

  ngOnInit(): void {
        if (this.jwtUtil.getId() != 0) this.router.navigateByUrl('')
    }

  errorMessage: string = ''

  public form: FormGroup = this.fb.group({
    email: new FormControl(null, [Validators.required, Validators.email]),
    password: new FormControl(null, [Validators.required]),
  });

  public goToPage(pageName: string) {
    this.router.navigate([`${pageName}`])
  }

  onSubmit() {
    if (this.form.valid) {
      this.service.signIn(this.form.value).subscribe(responseData => {
          if (responseData && responseData.data['accessToken']) {
            this.authService.setToken(responseData.data['accessToken']);
            this.location.back()
          }
        },
        error => {
          if (error.error) {
            this.errorMessage = error.error;
            console.log(this.errorMessage)
          }
        }
      );
    }
  }
}
<ng-container>
  <div class="flex flex-col items-center w-full bg-sky-950 justify-center" style="height: calc(100vh - 5rem)">
    <form [formGroup]="form" (ngSubmit)="onSubmit()" class="flex flex-col bg-white rounded w-4/12 p-4">
      <div class="mb-3">
        <label for="inputEmail" class="form-label">Email address</label>
        <input formControlName="email" type="email" class="border-blue-300 form-control" id="inputEmail"
               aria-describedby="emailHelp">
        <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
        <p class="text-danger" *ngIf="errorMessage === 'User not found'">*Incorrect email</p>
      </div>
      <div class="mb-3">
        <label for="inputPassword1" class="form-label">Password</label>
        <input formControlName="password" type="password" class="border-blue-300  form-control" id="inputPassword1">
        <p class="text-danger" *ngIf="errorMessage === 'Incorrect password'">*Incorrect password</p>
      </div>
      <button type="submit" class="btn btn-primary bg-sky-700" [disabled]="form.invalid ">Submit</button>

      <a  (click)="goToPage('/signup')" class="hover:text-sky-500 cursor-pointer flex self-center text-center mt-3">Don`t have account yet?</a>
    </form>
  </div>
</ng-container>
