import {Component, OnInit} from '@angular/core';
import {ActivatedRoute, Router} from "@angular/router";
import {JwtUtil} from "../../../util/JwtUtil";
import {UserData} from "../../../models/request/UserData";
import {PersonalService} from "../../../services/PersonalService";
import {AsyncPipe, NgForOf, NgIf, NgStyle} from "@angular/common";
import {FormsModule, ReactiveFormsModule} from "@angular/forms";
import {Observable} from "rxjs";
import {TeamService} from "../../../services/TeamService";
import { TeamData } from 'src/app/models/request/TeamData';

@Component({
  selector: 'app-user-profile',
  standalone: true,
  templateUrl: './user-profile.component.html',
  imports: [
    NgStyle,
    NgIf,
    FormsModule,
    ReactiveFormsModule,
    AsyncPipe,
    NgForOf
  ],
  styleUrls: ['./user-profile.component.css']
})
export class UserProfileComponent implements OnInit {

  public userId: number | undefined;
  public isOwner: boolean | undefined;
  public userData: UserData | undefined;
  public imageUrl: string | undefined;
  public isHasImage: boolean = false;
  public uploadImage: File | undefined;

  public avatarWindowOpen: boolean = false;
  public teams$ : Observable<TeamData[]> | undefined;

  public logoDataMap: { [key: number]: string } = {};
  public isHasLogoMap: {[key: number] : boolean} = {}
  public isLoaded = false;


  constructor(private route: ActivatedRoute,
              private jwtUtil: JwtUtil,
              private personalService:PersonalService,
              private teamService: TeamService,
              private router:Router,
  ) {


    this.route.params.subscribe(value => {
      this.userId = value['id']
    })

  }


  ngOnInit(): void {
    this.isOwner = this.jwtUtil.getId() == this.userId;
    this.isLoaded = false;
    this.personalService.getUserData(this.userId).subscribe(value => {
      this.userData = value;
      console.log(this.userData);
    },error => {
      if (error.error == "User not found") {
        this.router.navigateByUrl('error/404')
      }
    })
    this.teams$ = this.personalService.getTeamsByPersonal(this.userId);
    this.loadLogos();
    this.personalService.getAvatar(this.userId).subscribe( res =>{
        this.imageUrl = 'data:image/jpeg;base64,' + res.data;
        this.isHasImage = true;
        setTimeout(() => {
          this.isLoaded = true; // Установка isLoaded в true після затримки.
        }, 500); // Затримка в 500 мілісекунд (або можна вибрати інше значення затримки)
      }
    );
  }



  changeAvatar(){
    if (this.isOwner)
    this.avatarWindowOpen = true
  }

  public onImageUpload(event:Event) {
    if (event && event.target){
      // @ts-ignore
      this.uploadImage = event.target.files[0];
    }
  }
  public submitImage() {
    this.avatarWindowOpen = false;
    this.personalService.setPersonalAvatar(this.uploadImage, this.userId).subscribe(() =>{
      this.personalService.getAvatar(this.userId).subscribe( res =>{
          this.imageUrl = 'data:image/jpeg;base64,' + res.data;
          this.isHasImage = true;
        }
      )
      }

    )
  }

  loadLogos(): void {
    if (this.teams$)
    this.teams$.forEach(value => {
      value.forEach(value1 => {
        this.teamService.getTeamLogo(value1.id).subscribe(
          value2 => {
            this.logoDataMap[value1.id] = 'data:image/jpeg;base64,' + value2.data
            this.isHasLogoMap[value1.id] = true;
          }
          ,error => {
            this.isHasLogoMap[value1.id] = false
          }
        )
      })
    })

  }
  getLogo(id: number): string {
    return this.logoDataMap[id]
  }

  redirectToPage(url:string){
    this.router.navigateByUrl(url);
  }
}

<div class="flex flex-col relative items-center" style="min-height: calc(100vh - 5rem);
                                             background: rgb(2,0,36);
                                             background: linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(3,56,91,1) 81%);">
<!--  <img class="absolute left-50 top-48" src="../../../../assets/1496.gif" *ngIf="!isLoaded" alt="" width="64" height="64">-->
  <i *ngIf="!isLoaded" class="text-cyan-500 absolute left-50 top-48 fa-solid fa-spinner fa-spin"></i>


  <div class="flex w-5/6 h-56 items-center justify-center ">
    <div class="flex items-center">
      <img (click)="changeAvatar()" class="rounded-2xl" [class.hover:border-amber-300] ="isOwner" [class.cursor-pointer] ="isOwner" [class.hover:border-2] ="isOwner"  style="height: 80px; width: 80px;" *ngIf="isHasImage" [src]="imageUrl" alt="">
      <img (click)="changeAvatar()" [class.hover:border-amber-300] ="isOwner" [class.cursor-pointer] ="isOwner"  [class.hover:border-2] ="isOwner"  *ngIf="!isHasImage" class="rounded-2xl" style="height: 80px; width: 80px;" src="data:image/png;base64," alt="">
      <p class="text-cyan-50 font-semibold ml-3 text-2xl ">{{userData?.username}}</p>
    </div>
  </div>
  <ng-container *ngIf="teams$ | async as teams">
    <div class="lg:w-3/6 md:w-3/5 w-4/5 h-96 flex relative flex-col bg-cyan-100" style="overflow-y: auto;">
      <i *ngIf="isOwner" (click)="redirectToPage('teams/create')" title="Create new team" class="cursor-pointer fa-solid fa-plus absolute right-5 top-6 text-2xl"></i>
      <p *ngIf="isOwner" class="flex pb-3 justify-center text-4xl text-center font font-semibold mt-3 border-b-2 border-cyan-800">Your Teams</p>
      <p *ngIf="!isOwner" class="flex pb-3 justify-center text-4xl text-center font font-semibold mt-3 border-b-2 border-cyan-800">Teams</p>
      <div class="flex flex-col h-full w-full" style="max-height: 100%; overflow-y: auto;">
        <div (click)="redirectToPage('teams/'+team.id)" class="flex cursor-pointer items-center justify-center w-full min-h-1/5 bg-sky-600 hover:bg-sky-800 border-2 border-b-cyan-900" *ngFor="let team of teams">
          <img class="rounded-2xl mr-10" style="width: 50px; height: 50px" [src]="getLogo(team.id)" alt="">
          <p class="text-cyan-50 text-2xl">{{team.name}}</p>
        </div>
      </div>
    </div>

  </ng-container>





  <div *ngIf="avatarWindowOpen" class="z-2 left-0 top-0 absolute w-full h-screen flex items-center justify-center " style="background-color: rgba(100,100,100,0.4)">
    <div class="flex bg-white rounded w-2/6 h-50 flex-col">
      <i class="fa-solid fa-x text-red-700 m-4 font-bold text-4xl cursor-pointer" (click)="(avatarWindowOpen = false)"></i>
      <form (ngSubmit)="submitImage()" class="flex flex-col w-4/5 items-center mt-5 self-center">
        <input (change)="onImageUpload($event)" class="form-control border-blue-300" type="file">
        <button [disabled]="uploadImage == undefined" type="submit" class="btn btn-primary bg-sky-700 mt-10" >Set image</button>
      </form>
    </div>
  </div>

</div>
