          <div class="flex flex-col w-full h-50 relative cursor-pointer hover:bg-sky-600" (click)="navigateToTeamPage(match.team1.id)" *ngIf="match.team1">
            <i (click)="teamWon(match.team1.id, match.matchId, bracket.bracketId, stage.stageId)" *ngIf="role == 'ADMIN' && userId == adminId" class="fa-solid cursor-pointer fa-arrow-right absolute right-2 top-2 text-xl"></i>
