            <div class="flex w-full h-full justify-start items-center pl-3"><img class="rounded-3xl mr-3" *ngIf="isHasLogoMap[match.team2.id]" [src]="getLogo(match.team2.id)" style="width: 40px; height: 40px"  alt=""><p class="text-xl">{{ match.team2.name }}</p></div>
          </div>
