import { Component } from '@angular/core';
import {FormBuilder, FormControl, FormGroup, ReactiveFormsModule, Validators} from "@angular/forms";
import {TeamService} from "../../../../services/TeamService";

@Component({
  selector: 'app-team-creation',
  standalone: true,
  templateUrl: './team-creation.component.html',
  imports: [
    ReactiveFormsModule
  ],
  styleUrls: ['./team-creation.component.css']
})
export class TeamCreationComponent {

  uploadImage :File | undefined;

  constructor(private fb:FormBuilder,
              private teamService: TeamService,
  ) {
  }

  public form: FormGroup = this.fb.group({
    name: new FormControl(null, [Validators.required,Validators.minLength(4),Validators.maxLength(12)]),
    image: new FormControl(null, [Validators.required]),
  });

  public onSubmit() {
    if (this.form.valid) {
      this.teamService.createTeam(this.form.value.name,this.uploadImage)
    }
  }
  public onImageUpload(event:Event) {
    if (event && event.target){
      // @ts-ignore
      this.uploadImage = event.target.files[0];
    }
  }
}
<div class="flex flex-col items-center" style="min-height: calc(100vh - 5rem);
                                             background: rgb(2,0,36);
                                             background: linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(3,56,91,1) 81%);">

  <form [formGroup]="form" (ngSubmit)="onSubmit()" class="w-4/5 h-max pt-10 flex flex-col items-center">
    <p class="text-4xl text-cyan-50">Create team</p>
    <span class="flex flex-col mt-5 mb-3 lg:w-3/6 w-4/5">
      <p class="text-cyan-50 text-xl mb-3">Team name(4-12)</p>
      <input formControlName="name" class="bg-sky-800 rounded h-12 px-3 text-cyan-50" placeholder="Team name" type="text">
    </span>
    <span class="flex flex-col lg:w-3/6 w-4/5 ">
      <p class="text-cyan-50 text-xl mb-3">Team logo(50x50 recommended)</p>
      <input formControlName="image" (change)="onImageUpload($event)" class="pt-2 bg-sky-800 rounded h-12 px-3 text-cyan-50 cursor-pointer" type="file">
    </span>
    <button [disabled]="form.invalid " class="mt-4 lg:w-3/6 w-4/5 btn btn-success">Create</button>
  </form>
</div>
