import {Component, OnInit} from '@angular/core';
import {FormBuilder, FormControl, FormGroup, ReactiveFormsModule, Validators} from "@angular/forms";
import {AsyncPipe, Location, NgIf} from "@angular/common";
import {SignUpService} from "../../services/SignUpService";
import {Router} from "@angular/router";

import {AuthService} from "../../services/AuthService";

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  standalone: true,
  imports: [
    ReactiveFormsModule,
    NgIf,
    AsyncPipe,
  ],
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit{

  public passwordEquals: boolean | unknown= undefined ;

  public token: string | undefined;

  public form: FormGroup = this.fb.group({
    email: new FormControl(null, [Validators.required,Validators.email]),
    username: new FormControl(null, [Validators.required]),
    password1: new FormControl(null, [Validators.required]),
    password2: new FormControl(null, [Validators.required]),
  });

  constructor(private fb: FormBuilder,
              private service: SignUpService,
              private router: Router,
              private authService: AuthService,
              private location: Location) {
  }

  ngOnInit(): void {
        this.form.valueChanges.subscribe(value => {
          this.checkPasswordEquals(value['password1'],value['password2'])
        })
    }

  onSubmit() {
    if (this.form.valid) {
      this.service.signup(this.form.value).subscribe(registerData => {
        if(registerData && registerData['accessToken']) {
          this.authService.setToken(registerData['accessToken']);
          this.router.navigate(['/']).then(() => location.reload);
        }
      });
    }
  }

  private checkPasswordEquals(password1:string, password2:string) {
    if (password1 !== null && password2 !== null) {
      this.passwordEquals = password1 === password2;
    }
  }

}
<ng-container>
  <div class="flex flex-col items-center w-full bg-sky-950 justify-center" style="height: calc(100vh - 5rem)">
    <form [formGroup]="form" (ngSubmit)="onSubmit()" class="flex flex-col bg-white rounded w-4/12 p-4">
<!--      <h1 *ngIf="token">{{token}}</h1>-->
      <div class="mb-3">
        <label for="inputEmail" class="form-label">Email address</label>
        <input formControlName="email" type="email" class="border-blue-300 form-control" id="inputEmail" aria-describedby="emailHelp">
        <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
        <p class="text-danger" *ngIf="form.hasError">{{form.getError("email")}}</p>
      </div>
      <div class="mb-3">
        <label for="inputUsername" class="form-label">Username</label>
        <input formControlName="username" type="text" class="border-blue-300 form-control" id="inputUsername" aria-describedby="emailHelp">
      </div>
      <div class="mb-3">
        <label for="inputPassword1" class="form-label">Password</label>
        <input formControlName="password1" type="password" class="border-blue-300  form-control" id="inputPassword1">
      </div>

      <div class="mb-3">
        <label for="inputPassword2" class="form-label">Confirm password</label>
        <input formControlName="password2" type="password" class="border-blue-300  form-control" id="inputPassword2">
        <div class="text-danger" *ngIf="this.passwordEquals == false">
          *Passwords do not match
        </div>
      </div>

      <button type="submit" class="btn btn-primary bg-sky-700" [disabled]="form.invalid  || !this.passwordEquals ">Submit</button>
    </form>
  </div>
</ng-container>
